require_once ABSPATH . 'wp-admin/includes/post.php';
require_once ABSPATH . 'wp-admin/includes/theme.php';
require_once ABSPATH . 'wp-admin/includes/template.php';

if ( ! function_exists( 'beats_normalize_block_markup' ) ) {
    function beats_normalize_block_markup( $markup ) {
        if ( function_exists( 'parse_blocks' ) && function_exists( 'serialize_blocks' ) ) {
            $blocks = parse_blocks( $markup );
            if ( ! empty( $blocks ) ) {
                return serialize_blocks( $blocks );
            }
        }
        return $markup;
    }
}

if ( function_exists( 'beats_prime_data' ) ) {
    beats_prime_data();
} elseif ( function_exists( 'beats_seed_playground_demo' ) ) {
    beats_seed_playground_demo();
}

$beats_content = <<<'HTML'
<!-- wp:group {"tagName":"main","align":"full"} -->
<main class="wp-block-group alignfull">
<!-- wp:paragraph {"align":"center"} -->
<p class="has-text-align-center">Preview the Beats library and player below.</p>
<!-- /wp:paragraph -->
<!-- wp:beats/category-search /-->
<!-- wp:beats/visualizer /-->
<!-- wp:beats/display-home /-->
<!-- wp:beats/global-player /-->
</main>
<!-- /wp:group -->
HTML;
$beats_content = beats_normalize_block_markup( $beats_content );

$page_args = array(
    'post_title'   => 'Beats Demo',
    'post_name'    => 'beats-demo',
    'post_status'  => 'publish',
    'post_type'    => 'page',
    'post_content' => $beats_content,
    'post_author'  => get_current_user_id() ?: 1
);

$existing_page = get_page_by_path( 'beats-demo' );
if ( $existing_page ) {
    $page_args['ID'] = $existing_page->ID;
}

$page_id = wp_insert_post( $page_args, true );
if ( ! is_wp_error( $page_id ) ) {
    update_option( 'show_on_front', 'page' );
    update_option( 'page_on_front', $page_id );
    update_option( 'page_for_posts', 0 );
    echo 'Beats Demo front page ready (post ID ' . $page_id . ').' . PHP_EOL;
}

$upload_content = <<<'HTML'
<!-- wp:group {"tagName":"main","align":"full"} -->
<main class="wp-block-group alignfull">
<!-- wp:paragraph {"align":"center"} -->
<p class="has-text-align-center">Share a new beat with the Playground uploader.</p>
<!-- /wp:paragraph -->
<!-- wp:beats/upload-form /-->
</main>
<!-- /wp:group -->
HTML;
$upload_content = beats_normalize_block_markup( $upload_content );

$upload_args = array(
    'post_title'   => 'Upload',
    'post_name'    => 'upload',
    'post_status'  => 'publish',
    'post_type'    => 'page',
    'post_content' => $upload_content,
    'post_author'  => get_current_user_id() ?: 1
);

$existing_upload = get_page_by_path( 'upload' );
if ( $existing_upload ) {
    $upload_args['ID'] = $existing_upload->ID;
}

wp_insert_post( $upload_args, true );

update_option( 'blogname', 'Beats' );
