require_once ABSPATH . 'wp-admin/includes/post.php';

$template_contents = <<<'HTML'
<!-- wp:template-part {"slug":"header","tagName":"header"} /-->
<!-- wp:group {"tagName":"main","align":"full","style":{"spacing":{"padding":{"top":"0","bottom":"0"}}}} -->
<main class="wp-block-group alignfull" style="padding-top:0;padding-bottom:0">
<!-- wp:post-content {"layout":{"type":"constrained"}} /-->
</main>
<!-- /wp:group -->
<!-- wp:template-part {"slug":"footer","tagName":"footer"} /-->
HTML;

$theme       = wp_get_theme();
$theme_slug  = $theme->get_stylesheet();
$template_id = sprintf( '%s//front-page', $theme_slug );

$template_args = array(
    'post_title'   => 'Custom Front Page',
    'post_name'    => $template_id,
    'post_status'  => 'publish',
    'post_type'    => 'wp_template',
    'post_content' => $template_contents,
    'meta_input'   => array(
        'origin' => 'custom'
    )
);

$existing_template = get_page_by_path( $template_id, OBJECT, 'wp_template' );
if ( $existing_template ) {
    $template_args['ID'] = $existing_template->ID;
    $saved_id            = wp_update_post( $template_args );
} else {
    $saved_id = wp_insert_post( $template_args );
}

if ( ! is_wp_error( $saved_id ) ) {
    wp_set_post_terms( $saved_id, $theme_slug, 'wp_theme' );
    echo 'Stored custom front-page template in the database (post ID ' . $saved_id . ').' . PHP_EOL;
} else {
    echo 'Failed to store custom front-page template: ' . $saved_id->get_error_message() . PHP_EOL;
}

require_once ABSPATH . 'wp-admin/includes/post.php';
require_once ABSPATH . 'wp-admin/includes/misc.php';
require_once ABSPATH . 'wp-admin/includes/rewrite.php';

$category_html      = do_shortcode( '[beats_category_search]' );
$visualizer_html    = do_shortcode( '[beats_visualizer]' );
$display_home_html  = do_shortcode( '[beats_display_home]' );
$global_player_html = do_shortcode( '[beats_global_player]' );

$content = <<<HTML
<!-- wp:group {"tagName":"main","align":"full","style":{"spacing":{"padding":{"top":"30px","right":"20px","bottom":"40px","left":"20px"}}}} -->
<main class="wp-block-group alignfull" style="padding-top:30px;padding-right:20px;padding-bottom:40px;padding-left:20px">
<!-- wp:group {"align":"wide","anchor":"beats-wrapper","style":{"spacing":{"blockGap":"18px","margin":{"top":"0","bottom":"0"},"padding":{"top":"20px","right":"20px","bottom":"20px","left":"20px"}},"border":{"radius":"16px","color":"#f1f1f1","width":"1px"}},"layout":{"type":"constrained"}} -->
<div class="wp-block-group alignwide" id="beats-wrapper" style="border-color:#f1f1f1;border-width:1px;border-radius:16px;margin-top:0;margin-bottom:0;padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px">
<!-- wp:heading {"textAlign":"center"} -->
<h2 class="wp-block-heading has-text-align-center">Beats</h2>
<!-- /wp:heading -->
<!-- wp:paragraph {"align":"center"} -->
<p class="has-text-align-center">Preview every Beats shortcode below.</p>
<!-- /wp:paragraph -->
<!-- wp:group {"style":{"spacing":{"blockGap":"12px"}},"layout":{"type":"constrained"}} -->
<div class="wp-block-group" style="gap:12px">
$category_html
$visualizer_html
$display_home_html
$global_player_html
</div>
<!-- /wp:group -->
</div>
<!-- /wp:group -->
</main>
<!-- /wp:group -->
HTML;

$page_args = array(
    'post_title'   => 'Beats',
    'post_name'    => 'beats-playground',
    'post_status'  => 'publish',
    'post_type'    => 'page',
    'post_content' => $content
);

$existing = get_page_by_path( $page_args['post_name'], OBJECT, 'page' );
if ( $existing ) {
    $page_args['ID'] = $existing->ID;
    $page_id = wp_update_post( $page_args );
} else {
    $page_id = wp_insert_post( $page_args );
}

if ( is_wp_error( $page_id ) ) {
    echo 'Failed to create Beats homepage: ' . $page_id->get_error_message() . PHP_EOL;
} else {
    update_option( 'show_on_front', 'page' );
    update_option( 'page_on_front', $page_id );
    update_option( 'page_for_posts', 0 );
    flush_rewrite_rules( true );
    $prime_urls = array(
        home_url( '/?t=' . time() ),
        home_url( '/beats-playground/?t=' . ( time() + 1 ) ),
    );
    foreach ( $prime_urls as $url ) {
        $response = wp_remote_get( $url );
        if ( is_wp_error( $response ) ) {
            echo 'Priming request failed for ' . $url . ': ' . $response->get_error_message() . PHP_EOL;
        } else {
            echo 'Primed ' . $url . PHP_EOL;
        }
    }
    echo 'Beats homepage ready at /beats-playground/ (ID ' . $page_id . ').' . PHP_EOL;
}
