require_once ABSPATH . 'wp-admin/includes/post.php';

$page_args = array(
    'post_title'   => 'Beats Playground',
    'post_name'    => 'beats-playground',
    'post_status'  => 'publish',
    'post_type'    => 'page',
    'post_content' => '<!-- wp:shortcode -->[beats_player_demo]<!-- /wp:shortcode -->'
);

$existing = get_page_by_path( $page_args['post_name'], OBJECT, 'page' );
if ( $existing ) {
    $page_args['ID'] = $existing->ID;
    $page_id = wp_update_post( $page_args );
} else {
    $page_id = wp_insert_post( $page_args );
}

update_option( 'show_on_front', 'page' );
update_option( 'page_on_front', $page_id );

echo 'Beats Playground page ready with ID ' . $page_id . PHP_EOL;
