require_once ABSPATH . 'wp-admin/includes/post.php';
require_once ABSPATH . 'wp-admin/includes/theme.php';
require_once ABSPATH . 'wp-admin/includes/template.php';

if ( function_exists( 'beats_prime_data' ) ) {
    beats_prime_data();
} elseif ( function_exists( 'beats_seed_playground_demo' ) ) {
    beats_seed_playground_demo();
}

$beats_content = <<<HTML
<!-- wp:group {"tagName":"main","align":"full","style":{"spacing":{"padding":{"top":"30px","right":"20px","bottom":"40px","left":"20px"}}}} -->
<main class="wp-block-group alignfull" style="padding-top:30px;padding-right:20px;padding-bottom:40px;padding-left:20px">
<!-- wp:group {"align":"wide","anchor":"beats-wrapper","style":{"spacing":{"blockGap":"18px","margin":{"top":"0","bottom":"0"},"padding":{"top":"20px","right":"20px","bottom":"20px","left":"20px"}},"border":{"radius":"16px","color":"#f1f1f1","width":"1px"}},"layout":{"type":"constrained"}} -->
<div class="wp-block-group alignwide" id="beats-wrapper" style="border-color:#f1f1f1;border-width:1px;border-radius:16px;margin-top:0;margin-bottom:0;padding-top:20px;padding-right:20px;padding-bottom:20px;padding-left:20px">
<!-- wp:heading {"textAlign":"center"} -->
<h2 class="wp-block-heading has-text-align-center">Beats</h2>
<!-- /wp:heading -->
<!-- wp:paragraph {"align":"center"} -->
<p class="has-text-align-center">Preview every Beats shortcode below.</p>
<!-- /wp:paragraph -->
<!-- wp:group {"style":{"spacing":{"blockGap":"12px"}},"layout":{"type":"constrained"}} -->
<div class="wp-block-group" style="gap:12px">
<!-- wp:shortcode -->[beats_category_search]<!-- /wp:shortcode -->
<!-- wp:shortcode -->[beats_visualizer]<!-- /wp:shortcode -->
<!-- wp:shortcode -->[beats_display_home]<!-- /wp:shortcode -->
<!-- wp:shortcode -->[beats_global_player]<!-- /wp:shortcode -->
</div>
<!-- /wp:group -->
</div>
<!-- /wp:group -->
</main>
<!-- /wp:group -->
HTML;

$page_args = array(
    'post_title'   => 'Beats Demo',
    'post_name'    => 'beats-demo',
    'post_status'  => 'publish',
    'post_type'    => 'page',
    'post_content' => $beats_content,
    'post_author'  => get_current_user_id() ?: 1
);

$existing_page = get_page_by_path( 'beats-demo' );
if ( $existing_page ) {
    $page_args['ID'] = $existing_page->ID;
    $page_id         = wp_update_post( $page_args );
} else {
    $page_id = wp_insert_post( $page_args );
}

if ( ! is_wp_error( $page_id ) ) {
    update_option( 'show_on_front', 'page' );
    update_option( 'page_on_front', $page_id );
    update_option( 'page_for_posts', 0 );
    echo 'Beats Demo front page ready (post ID ' . $page_id . ').' . PHP_EOL;
} else {
    echo 'Failed to create Beats Demo page: ' . $page_id->get_error_message() . PHP_EOL;
}
